    private void buttonSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonSaveActionPerformed
        String studentID = studentidtxt.getText();
        String name = nametxt.getText();
        String fatherName = fathernametxt.getText();
        String courseName = (String) coursenametxt.getSelectedItem();
        String branchName = (String) branchnametxt.getSelectedItem();
        
        try {
            Connection conn = ConnectionProvider.getCon();
            Statement st = conn.createStatement();
            st.executeUpdate("insert into student values('"+studentID+"','"+name+"','"+fatherName+"','"+courseName+"','"+branchName+"')");
            JOptionPane.showMessageDialog(null, "Successfully Added");
            setVisible(false);
            new newStudent().setVisible(true);
        } catch (HeadlessException | SQLException e) {
            JOptionPane.showMessageDialog(null, "Student Id already exists");
            setVisible(false);
            new newStudent().setVisible(true);
        }
    }//GEN-LAST:event_buttonSaveActionPerformed

